import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Search, Filter, SortDesc, ExternalLink, Heart, MessageCircle, TrendingUp } from 'lucide-react';
import SEOHead from '../components/SEOHead';

interface Domain {
  id: string;
  name: string;
  price: string;
  currency: string;
  description: string;
  category: string;
  views: number;
  likes: number;
  isVerified: boolean;
  image?: string;
}

// Mock data for now - will be replaced with Doma API
const mockDomains: Domain[] = [
  {
    id: '1',
    name: 'crypto.doma',
    price: '5.5',
    currency: 'ETH',
    description: 'Premium crypto domain perfect for DeFi projects',
    category: 'Finance',
    views: 1250,
    likes: 89,
    isVerified: true,
  },
  {
    id: '2',
    name: 'nft.doma',
    price: '3.2',
    currency: 'ETH',
    description: 'Ideal for NFT marketplaces and collections',
    category: 'NFT',
    views: 980,
    likes: 67,
    isVerified: true,
  },
  {
    id: '3',
    name: 'gaming.doma',
    price: '2.8',
    currency: 'ETH',
    description: 'Perfect domain for Web3 gaming platforms',
    category: 'Gaming',
    views: 756,
    likes: 45,
    isVerified: false,
  },
  {
    id: '4',
    name: 'defi.doma',
    price: '7.1',
    currency: 'ETH',
    description: 'Premium DeFi domain with high brand value',
    category: 'Finance',
    views: 1580,
    likes: 123,
    isVerified: true,
  },
];

const Marketplace: React.FC = () => {
  const [domains, setDomains] = useState<Domain[]>(mockDomains);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortBy, setSortBy] = useState('popularity');
  const [showFilters, setShowFilters] = useState(false);

  // GraphQL queries
  const { data: domainsData, loading: domainsLoading, error: domainsError } = useQuery(GET_DOMAINS, {
    variables: { take: 20, skip: 0, sortOrder: 'DESC' },
    errorPolicy: 'all'
  });

  const { data: statsData } = useQuery(GET_STATISTICS, {
    errorPolicy: 'all'
  });

  // Transform Doma API data to our domain format
  useEffect(() => {
    if (domainsData?.names?.items) {
      const transformedDomains: Domain[] = domainsData.names.items.map((item: any, index: number) => ({
        id: item.tokenId || index.toString(),
        name: item.name,
        price: (Math.random() * 10 + 1).toFixed(1), // Mock price for now
        currency: 'ETH',
        description: `Premium domain ${item.name} on the Doma Protocol`,
        category: item.name.includes('crypto') ? 'Finance' : 
                 item.name.includes('nft') ? 'NFT' : 
                 item.name.includes('game') ? 'Gaming' : 'Technology',
        views: Math.floor(Math.random() * 2000) + 100,
        likes: Math.floor(Math.random() * 200) + 10,
        isVerified: Math.random() > 0.3, // 70% verified
      }));

      if (transformedDomains.length > 0) {
        setDomains(transformedDomains);
      }
    }
  }, [domainsData]);

  const categories = ['All', 'Finance', 'NFT', 'Gaming', 'Technology', 'Art'];
  const sortOptions = [
    { value: 'popularity', label: 'Most Popular' },
    { value: 'price-low', label: 'Price: Low to High' },
    { value: 'price-high', label: 'Price: High to Low' },
    { value: 'newest', label: 'Newest First' },
  ];

  const filteredDomains = domains.filter(domain => {
    const matchesSearch = domain.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         domain.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'All' || domain.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const sortedDomains = [...filteredDomains].sort((a, b) => {
    switch (sortBy) {
      case 'price-low':
        return parseFloat(a.price) - parseFloat(b.price);
      case 'price-high':
        return parseFloat(b.price) - parseFloat(a.price);
      case 'popularity':
        return b.views - a.views;
      case 'newest':
        return parseInt(b.id) - parseInt(a.id);
      default:
        return 0;
    }
  });

  return (
    <>
      <SEOHead 
        title="Marketplace - Premium Blockchain Domains | DomaMarket"
        description="Browse and buy premium blockchain domains on the Doma Protocol. Discover valuable digital assets with verified ownership and seamless trading."
        keywords="blockchain domains, domain marketplace, buy domains, Doma Protocol, Web3 domains, crypto domains"
      />
      <div className="px-4 sm:px-0">
      {/* Hero Section */}
      <div className="text-center py-12 bg-gradient-to-r from-primary-50 to-secondary-50 rounded-2xl mb-8">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">
          Discover Premium <span className="text-primary-600">Blockchain Domains</span>
        </h1>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto mb-8">
          Buy, sell, and trade high-value domain names on the Doma Protocol. 
          Connect with the community and build your digital presence.
        </p>
        <div className="flex flex-col sm:flex-row gap-4 justify-center max-w-md mx-auto">
          <button className="bg-primary-600 hover:bg-primary-700 text-white px-6 py-3 rounded-lg font-medium transition-colors">
            Browse Domains
          </button>
          <button className="border border-primary-600 text-primary-600 hover:bg-primary-50 px-6 py-3 rounded-lg font-medium transition-colors">
            List Your Domain
          </button>
        </div>
      </div>

      {/* Search and Filters */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
        <div className="flex flex-col lg:flex-row gap-4">
          {/* Search */}
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Search domains..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              />
            </div>
          </div>

          {/* Category Filter */}
          <div className="lg:w-48">
            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {categories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>

          {/* Sort */}
          <div className="lg:w-48">
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
            >
              {sortOptions.map(option => (
                <option key={option.value} value={option.value}>{option.label}</option>
              ))}
            </select>
          </div>

          {/* Filter Toggle */}
          <button
            onClick={() => setShowFilters(!showFilters)}
            className="lg:w-auto flex items-center justify-center px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
          >
            <Filter className="w-5 h-5 mr-2" />
            Filters
          </button>
        </div>

        {/* Advanced Filters (collapsible) */}
        {showFilters && (
          <div className="mt-6 pt-6 border-t border-gray-200">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Price Range</label>
                <div className="flex gap-2">
                  <input
                    type="number"
                    placeholder="Min"
                    className="flex-1 px-3 py-2 border border-gray-300 rounded-lg text-sm"
                  />
                  <input
                    type="number"
                    placeholder="Max"
                    className="flex-1 px-3 py-2 border border-gray-300 rounded-lg text-sm"
                  />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Length</label>
                <select className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm">
                  <option value="">Any Length</option>
                  <option value="short">Short (3-5 chars)</option>
                  <option value="medium">Medium (6-10 chars)</option>
                  <option value="long">Long (11+ chars)</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Status</label>
                <div className="space-y-2">
                  <label className="flex items-center">
                    <input type="checkbox" className="mr-2" />
                    <span className="text-sm">Verified Only</span>
                  </label>
                  <label className="flex items-center">
                    <input type="checkbox" className="mr-2" />
                    <span className="text-sm">Buy Now Available</span>
                  </label>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Stats */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        <div className="bg-white rounded-lg border border-gray-200 p-6 text-center">
          <div className="text-2xl font-bold text-primary-600">
            {statsData?.statistics?.totalDomains?.toLocaleString() || '2,847'}
          </div>
          <div className="text-sm text-gray-600">Domains Listed</div>
        </div>
        <div className="bg-white rounded-lg border border-gray-200 p-6 text-center">
          <div className="text-2xl font-bold text-secondary-600">
            {domainsLoading ? '...' : `${domainsData?.names?.totalCount || 156} domains`}
          </div>
          <div className="text-sm text-gray-600">Available</div>
        </div>
        <div className="bg-white rounded-lg border border-gray-200 p-6 text-center">
          <div className="text-2xl font-bold text-green-600">12.5 ETH</div>
          <div className="text-sm text-gray-600">Floor Price</div>
        </div>
        <div className="bg-white rounded-lg border border-gray-200 p-6 text-center">
          <div className="text-2xl font-bold text-purple-600">1,234</div>
          <div className="text-sm text-gray-600">Active Users</div>
        </div>
      </div>

      {/* Domain Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {sortedDomains.map(domain => (
          <DomainCard key={domain.id} domain={domain} />
        ))}
      </div>

      {/* Load More */}
      <div className="text-center mt-12">
        <button className="bg-primary-600 hover:bg-primary-700 text-white px-8 py-3 rounded-lg font-medium transition-colors">
          Load More Domains
        </button>
      </div>
      </div>
    </>
  );
};

const DomainCard: React.FC<{ domain: Domain }> = ({ domain }) => {
  const [isLiked, setIsLiked] = useState(false);

  return (
    <div className="bg-white rounded-lg border border-gray-200 overflow-hidden hover:shadow-lg transition-shadow">
      {/* Domain Image/Preview */}
      <div className="aspect-video bg-gradient-to-br from-primary-100 to-secondary-100 flex items-center justify-center">
        <div className="text-center">
          <div className="text-2xl font-bold text-gray-700">{domain.name}</div>
          {domain.isVerified && (
            <div className="mt-2 inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
              Verified
            </div>
          )}
        </div>
      </div>

      {/* Content */}
      <div className="p-4">
        <div className="flex items-start justify-between mb-2">
          <h3 className="font-semibold text-gray-900 truncate">{domain.name}</h3>
          <button
            onClick={() => setIsLiked(!isLiked)}
            className={`p-1 rounded-full transition-colors ${
              isLiked ? 'text-red-500' : 'text-gray-400 hover:text-red-500'
            }`}
          >
            <Heart className={`w-4 h-4 ${isLiked ? 'fill-current' : ''}`} />
          </button>
        </div>

        <p className="text-sm text-gray-600 mb-3 line-clamp-2">{domain.description}</p>

        <div className="flex items-center justify-between text-sm text-gray-500 mb-4">
          <span className="inline-flex items-center">
            <TrendingUp className="w-4 h-4 mr-1" />
            {domain.views} views
          </span>
          <span className="px-2 py-1 bg-gray-100 rounded-full text-xs">
            {domain.category}
          </span>
        </div>

        <div className="flex items-center justify-between">
          <div>
            <div className="text-lg font-bold text-gray-900">
              {domain.price} {domain.currency}
            </div>
            <div className="text-xs text-gray-500">â‰ˆ ${(parseFloat(domain.price) * 2400).toLocaleString()}</div>
          </div>
          <div className="flex gap-2">
            <button className="p-2 text-gray-500 hover:text-gray-700 transition-colors">
              <MessageCircle className="w-4 h-4" />
            </button>
            <button className="p-2 text-gray-500 hover:text-gray-700 transition-colors">
              <ExternalLink className="w-4 h-4" />
            </button>
          </div>
        </div>

        <div className="flex gap-2 mt-4">
          <Link 
            to={`/domain/${domain.name}`}
            className="flex-1 bg-primary-600 hover:bg-primary-700 text-white py-2 rounded-lg font-medium transition-colors text-center"
          >
            View Details
          </Link>
          <button className="flex-1 border border-primary-600 text-primary-600 hover:bg-primary-50 py-2 rounded-lg font-medium transition-colors">
            Make Offer
          </button>
        </div>
      </div>
    </div>
  );
};

export default Marketplace;
