import React from 'react';
import { Helmet } from 'react-helmet-async';

interface SEOHeadProps {
  title?: string;
  description?: string;
  keywords?: string;
  image?: string;
  url?: string;
  type?: string;
  domainName?: string;
}

const SEOHead: React.FC<SEOHeadProps> = ({
  title = 'DomaMarket - Premium Blockchain Domain Marketplace',
  description = 'Discover, buy, and sell premium blockchain domains on the Doma Protocol. Connect with the community and build your digital presence.',
  keywords = 'blockchain domains, domain marketplace, Doma Protocol, Web3 domains, NFT domains, crypto domains',
  image = '/og-image.jpg',
  url = window.location.href,
  type = 'website',
  domainName
}) => {
  const fullTitle = domainName ? `${domainName} - ${title}` : title;
  const fullDescription = domainName 
    ? `Premium domain ${domainName} available on DomaMarket. ${description}`
    : description;

  return (
    <Helmet>
      {/* Basic Meta Tags */}
      <title>{fullTitle}</title>
      <meta name="description" content={fullDescription} />
      <meta name="keywords" content={keywords} />
      <meta name="author" content="DomaMarket" />
      <meta name="robots" content="index, follow" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />

      {/* Open Graph Tags */}
      <meta property="og:title" content={fullTitle} />
      <meta property="og:description" content={fullDescription} />
      <meta property="og:image" content={image} />
      <meta property="og:url" content={url} />
      <meta property="og:type" content={type} />
      <meta property="og:site_name" content="DomaMarket" />

      {/* Twitter Card Tags */}
      <meta name="twitter:card" content="summary_large_image" />
      <meta name="twitter:title" content={fullTitle} />
      <meta name="twitter:description" content={fullDescription} />
      <meta name="twitter:image" content={image} />
      <meta name="twitter:site" content="@DomaMarket" />

      {/* Additional SEO Tags */}
      <link rel="canonical" href={url} />
      <meta name="theme-color" content="#0ea5e9" />

      {/* Schema.org JSON-LD for Domain Marketplace */}
      <script type="application/ld+json">
        {JSON.stringify({
          "@context": "https://schema.org",
          "@type": domainName ? "Product" : "WebSite",
          "name": domainName || "DomaMarket",
          "description": fullDescription,
          "url": url,
          "image": image,
          ...(domainName && {
            "category": "Domain Name",
            "brand": {
              "@type": "Brand",
              "name": "Doma Protocol"
            },
            "offers": {
              "@type": "Offer",
              "availability": "https://schema.org/InStock",
              "priceCurrency": "ETH"
            }
          }),
          ...(!domainName && {
            "potentialAction": {
              "@type": "SearchAction",
              "target": {
                "@type": "EntryPoint",
                "urlTemplate": `${window.location.origin}/?search={search_term_string}`
              },
              "query-input": "required name=search_term_string"
            }
          })
        })}
      </script>
    </Helmet>
  );
};

export default SEOHead;
