import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { 
  Heart, 
  Share2, 
  ExternalLink, 
  MessageCircle, 
  TrendingUp, 
  Calendar,
  User,
  CheckCircle,
  AlertTriangle,
  BarChart3
} from 'lucide-react';

interface DomainData {
  id: string;
  name: string;
  price: string;
  currency: string;
  description: string;
  category: string;
  views: number;
  likes: number;
  isVerified: boolean;
  owner: string;
  createdAt: string;
  expiresAt: string;
  traits: { [key: string]: string };
  analytics: {
    pageViews: number;
    uniqueVisitors: number;
    conversionRate: number;
    averageTimeSpent: string;
  };
  offers: Array<{
    id: string;
    bidder: string;
    amount: string;
    currency: string;
    timestamp: string;
    status: 'pending' | 'accepted' | 'rejected';
  }>;
}

// Mock domain data
const mockDomain: DomainData = {
  id: '1',
  name: 'crypto.doma',
  price: '5.5',
  currency: 'ETH',
  description: 'Premium crypto domain perfect for DeFi projects, exchanges, and cryptocurrency businesses. This domain has excellent SEO potential and brand recognition in the crypto space.',
  category: 'Finance',
  views: 1250,
  likes: 89,
  isVerified: true,
  owner: '0x1234...5678',
  createdAt: '2024-01-15',
  expiresAt: '2025-01-15',
  traits: {
    'Length': '6 characters',
    'Extension': '.doma',
    'Subdomain Support': 'Yes',
    'SSL Included': 'Yes',
    'DNS Management': 'Full control'
  },
  analytics: {
    pageViews: 15420,
    uniqueVisitors: 8930,
    conversionRate: 2.3,
    averageTimeSpent: '3:42'
  },
  offers: [
    {
      id: '1',
      bidder: '0xabcd...efgh',
      amount: '4.8',
      currency: 'ETH',
      timestamp: '2024-03-10T10:30:00Z',
      status: 'pending'
    },
    {
      id: '2',
      bidder: '0x9876...5432',
      amount: '5.0',
      currency: 'ETH',
      timestamp: '2024-03-09T15:22:00Z',
      status: 'pending'
    }
  ]
};

const DomainDetail: React.FC = () => {
  const { domainName } = useParams<{ domainName: string }>();
  const [domain] = useState<DomainData>(mockDomain);
  const [isLiked, setIsLiked] = useState(false);
  const [offerAmount, setOfferAmount] = useState('');
  const [showOfferForm, setShowOfferForm] = useState(false);
  const [activeTab, setActiveTab] = useState('overview');

  const tabs = [
    { id: 'overview', label: 'Overview' },
    { id: 'analytics', label: 'Analytics' },
    { id: 'offers', label: 'Offers & Bids' },
    { id: 'history', label: 'History' }
  ];

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Main Content */}
        <div className="lg:col-span-2 space-y-8">
          {/* Domain Header */}
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
            {/* Domain Preview */}
            <div className="aspect-video bg-gradient-to-br from-primary-100 to-secondary-100 flex items-center justify-center">
              <div className="text-center">
                <h1 className="text-4xl font-bold text-gray-800 mb-2">{domain.name}</h1>
                {domain.isVerified && (
                  <div className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-green-100 text-green-800">
                    <CheckCircle className="w-4 h-4 mr-1" />
                    Verified Domain
                  </div>
                )}
              </div>
            </div>

            {/* Domain Info */}
            <div className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div>
                  <h2 className="text-2xl font-bold text-gray-900">{domain.name}</h2>
                  <p className="text-gray-600 mt-1">{domain.description}</p>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => setIsLiked(!isLiked)}
                    className={`p-2 rounded-lg transition-colors ${
                      isLiked ? 'bg-red-50 text-red-600' : 'bg-gray-50 text-gray-600 hover:bg-gray-100'
                    }`}
                  >
                    <Heart className={`w-5 h-5 ${isLiked ? 'fill-current' : ''}`} />
                  </button>
                  <button className="p-2 bg-gray-50 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors">
                    <Share2 className="w-5 h-5" />
                  </button>
                  <button className="p-2 bg-gray-50 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors">
                    <ExternalLink className="w-5 h-5" />
                  </button>
                </div>
              </div>

              {/* Stats */}
              <div className="grid grid-cols-3 gap-4 py-4 border-t border-b border-gray-200">
                <div className="text-center">
                  <div className="text-lg font-semibold text-gray-900">{domain.views.toLocaleString()}</div>
                  <div className="text-sm text-gray-600">Views</div>
                </div>
                <div className="text-center">
                  <div className="text-lg font-semibold text-gray-900">{domain.likes}</div>
                  <div className="text-sm text-gray-600">Likes</div>
                </div>
                <div className="text-center">
                  <div className="text-lg font-semibold text-gray-900">{domain.offers.length}</div>
                  <div className="text-sm text-gray-600">Offers</div>
                </div>
              </div>

              {/* Tabs */}
              <div className="mt-6">
                <div className="border-b border-gray-200">
                  <nav className="-mb-px flex space-x-8">
                    {tabs.map((tab) => (
                      <button
                        key={tab.id}
                        onClick={() => setActiveTab(tab.id)}
                        className={`py-2 px-1 border-b-2 font-medium text-sm transition-colors ${
                          activeTab === tab.id
                            ? 'border-primary-500 text-primary-600'
                            : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                        }`}
                      >
                        {tab.label}
                      </button>
                    ))}
                  </nav>
                </div>

                {/* Tab Content */}
                <div className="mt-6">
                  {activeTab === 'overview' && (
                    <div className="space-y-6">
                      <div>
                        <h3 className="text-lg font-semibold text-gray-900 mb-3">Domain Traits</h3>
                        <div className="grid grid-cols-2 gap-4">
                          {Object.entries(domain.traits).map(([key, value]) => (
                            <div key={key} className="flex justify-between p-3 bg-gray-50 rounded-lg">
                              <span className="text-gray-600">{key}</span>
                              <span className="font-medium text-gray-900">{value}</span>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  )}

                  {activeTab === 'analytics' && (
                    <div className="space-y-6">
                      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
                        <div className="bg-blue-50 p-4 rounded-lg">
                          <div className="text-2xl font-bold text-blue-600">{domain.analytics.pageViews.toLocaleString()}</div>
                          <div className="text-sm text-blue-800">Page Views</div>
                        </div>
                        <div className="bg-green-50 p-4 rounded-lg">
                          <div className="text-2xl font-bold text-green-600">{domain.analytics.uniqueVisitors.toLocaleString()}</div>
                          <div className="text-sm text-green-800">Unique Visitors</div>
                        </div>
                        <div className="bg-purple-50 p-4 rounded-lg">
                          <div className="text-2xl font-bold text-purple-600">{domain.analytics.conversionRate}%</div>
                          <div className="text-sm text-purple-800">Conversion Rate</div>
                        </div>
                        <div className="bg-orange-50 p-4 rounded-lg">
                          <div className="text-2xl font-bold text-orange-600">{domain.analytics.averageTimeSpent}</div>
                          <div className="text-sm text-orange-800">Avg. Time</div>
                        </div>
                      </div>
                    </div>
                  )}

                  {activeTab === 'offers' && (
                    <div className="space-y-4">
                      {domain.offers.map((offer) => (
                        <div key={offer.id} className="flex items-center justify-between p-4 border border-gray-200 rounded-lg">
                          <div className="flex items-center gap-4">
                            <div className="w-10 h-10 bg-gray-100 rounded-full flex items-center justify-center">
                              <User className="w-5 h-5 text-gray-600" />
                            </div>
                            <div>
                              <div className="font-medium text-gray-900">{offer.bidder}</div>
                              <div className="text-sm text-gray-600">
                                {new Date(offer.timestamp).toLocaleDateString()}
                              </div>
                            </div>
                          </div>
                          <div className="text-right">
                            <div className="font-bold text-gray-900">{offer.amount} {offer.currency}</div>
                            <div className={`text-sm ${
                              offer.status === 'pending' ? 'text-yellow-600' :
                              offer.status === 'accepted' ? 'text-green-600' : 'text-red-600'
                            }`}>
                              {offer.status.charAt(0).toUpperCase() + offer.status.slice(1)}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}

                  {activeTab === 'history' && (
                    <div className="space-y-4">
                      <div className="text-center text-gray-500 py-8">
                        <BarChart3 className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                        <p>Transaction history will be displayed here</p>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Purchase Card */}
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
            <div className="text-center mb-6">
              <div className="text-3xl font-bold text-gray-900 mb-1">
                {domain.price} {domain.currency}
              </div>
              <div className="text-gray-600">
                ≈ ${(parseFloat(domain.price) * 2400).toLocaleString()} USD
              </div>
            </div>

            <div className="space-y-3">
              <button className="w-full bg-primary-600 hover:bg-primary-700 text-white py-3 rounded-lg font-medium transition-colors">
                Buy Now
              </button>
              <button 
                onClick={() => setShowOfferForm(!showOfferForm)}
                className="w-full border border-primary-600 text-primary-600 hover:bg-primary-50 py-3 rounded-lg font-medium transition-colors"
              >
                Make Offer
              </button>
              <button className="w-full border border-gray-300 text-gray-700 hover:bg-gray-50 py-3 rounded-lg font-medium transition-colors flex items-center justify-center">
                <MessageCircle className="w-4 h-4 mr-2" />
                Contact Owner
              </button>
            </div>

            {/* Offer Form */}
            {showOfferForm && (
              <div className="mt-6 pt-6 border-t border-gray-200">
                <h4 className="font-medium text-gray-900 mb-3">Make an Offer</h4>
                <div className="space-y-3">
                  <div>
                    <input
                      type="number"
                      placeholder="Enter amount"
                      value={offerAmount}
                      onChange={(e) => setOfferAmount(e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    />
                  </div>
                  <div className="flex gap-2">
                    <button className="flex-1 bg-primary-600 hover:bg-primary-700 text-white py-2 rounded-lg font-medium transition-colors">
                      Submit Offer
                    </button>
                    <button 
                      onClick={() => setShowOfferForm(false)}
                      className="px-4 py-2 border border-gray-300 text-gray-700 hover:bg-gray-50 rounded-lg transition-colors"
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Owner Info */}
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
            <h3 className="font-semibold text-gray-900 mb-4">Owner Information</h3>
            <div className="flex items-center gap-3 mb-4">
              <div className="w-12 h-12 bg-gradient-to-br from-primary-400 to-secondary-400 rounded-full flex items-center justify-center">
                <User className="w-6 h-6 text-white" />
              </div>
              <div>
                <div className="font-medium text-gray-900">{domain.owner}</div>
                <div className="text-sm text-gray-600">Domain Owner</div>
              </div>
            </div>
            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-600">Domains Owned:</span>
                <span className="font-medium text-gray-900">127</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Member Since:</span>
                <span className="font-medium text-gray-900">Jan 2024</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Rating:</span>
                <span className="font-medium text-gray-900">4.8/5</span>
              </div>
            </div>
          </div>

          {/* Domain Details */}
          <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
            <h3 className="font-semibold text-gray-900 mb-4">Domain Details</h3>
            <div className="space-y-3 text-sm">
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Created:</span>
                <span className="font-medium text-gray-900">{new Date(domain.createdAt).toLocaleDateString()}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Expires:</span>
                <span className="font-medium text-gray-900">{new Date(domain.expiresAt).toLocaleDateString()}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Category:</span>
                <span className="px-2 py-1 bg-primary-100 text-primary-800 rounded-full text-xs font-medium">
                  {domain.category}
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DomainDetail;
