import { GraphQLClient } from 'graphql-request';

// Doma Protocol API Configuration
const DOMA_CONFIG = {
  testnet: {
    chainId: '97476',
    currency: 'ETH',
    bridge: 'https://bridge-testnet.doma.xyz',
    rpc: 'https://rpc-testnet.doma.xyz',
    explorer: 'https://explorer-testnet.doma.xyz',
    api: 'https://api-testnet.doma.xyz',
    subgraph: 'https://api-testnet.doma.xyz/graphql'
  }
};

// GraphQL client for subgraph queries
export const createGraphQLClient = (apiKey?: string) => {
  const headers: Record<string, string> = {
    'Content-Type': 'application/json',
  };

  if (apiKey) {
    headers['Api-Key'] = apiKey;
  }

  return new GraphQLClient(DOMA_CONFIG.testnet.subgraph, {
    headers
  });
};

// REST API client for orderbook and other operations
export class DomaAPIClient {
  private baseUrl: string;
  private apiKey?: string;

  constructor(apiKey?: string) {
    this.baseUrl = DOMA_CONFIG.testnet.api;
    this.apiKey = apiKey;
  }

  private async request<T>(endpoint: string, options: RequestInit = {}): Promise<T> {
    const headers: Record<string, string> = {
      'Content-Type': 'application/json',
      ...options.headers as Record<string, string>
    };

    if (this.apiKey) {
      headers['Api-Key'] = this.apiKey;
    }

    const response = await fetch(`${this.baseUrl}${endpoint}`, {
      ...options,
      headers
    });

    if (!response.ok) {
      throw new Error(`API request failed: ${response.status} ${response.statusText}`);
    }

    return response.json();
  }

  // Orderbook API methods
  async createListing(params: {
    orderbook: string;
    chainId: string;
    parameters: any;
    signature: string;
  }) {
    return this.request<{ orderId: string }>('/v1/orderbook/list', {
      method: 'POST',
      body: JSON.stringify(params)
    });
  }

  async createOffer(params: {
    orderbook: string;
    chainId: string;
    parameters: any;
    signature: string;
  }) {
    return this.request<{ orderId: string }>('/v1/orderbook/offer', {
      method: 'POST',
      body: JSON.stringify(params)
    });
  }

  async getListingFulfillmentData(orderId: string, buyer: string) {
    return this.request<{ parameters: any; signature: string }>(
      `/v1/orderbook/listing/${orderId}/${buyer}`
    );
  }

  async getOfferFulfillmentData(orderId: string, fulfiller: string) {
    return this.request<{ parameters: any; signature: string }>(
      `/v1/orderbook/offer/${orderId}/${fulfiller}`
    );
  }

  async cancelListing(params: { orderId: string; signature: string }) {
    return this.request<{ orderId: string }>('/v1/orderbook/listing/cancel', {
      method: 'POST',
      body: JSON.stringify(params)
    });
  }

  async cancelOffer(params: { orderId: string; signature: string }) {
    return this.request<{ orderId: string }>('/v1/orderbook/offer/cancel', {
      method: 'POST',
      body: JSON.stringify(params)
    });
  }

  async getOrderbookFees(orderbook: string, chainId: string, contractAddress: string) {
    return this.request<{ marketplaceFees: any[] }>(
      `/v1/orderbook/fee/${orderbook}/${chainId}/${contractAddress}`
    );
  }

  async getSupportedCurrencies(chainId: string, contractAddress: string, orderbook: string) {
    return this.request<{ currencies: any[] }>(
      `/v1/orderbook/currencies/${chainId}/${contractAddress}/${orderbook}`
    );
  }

  // Poll API methods for real-time events
  async pollEvents(params?: {
    eventTypes?: string[];
    limit?: number;
    finalizedOnly?: boolean;
  }) {
    const searchParams = new URLSearchParams();

    if (params?.eventTypes) {
      params.eventTypes.forEach(type => searchParams.append('eventTypes', type));
    }
    if (params?.limit) {
      searchParams.append('limit', params.limit.toString());
    }
    if (params?.finalizedOnly !== undefined) {
      searchParams.append('finalizedOnly', params.finalizedOnly.toString());
    }

    return this.request<{
      events: any[];
      lastId: number;
      hasMoreEvents: boolean;
    }>(`/v1/poll?${searchParams.toString()}`);
  }

  async acknowledgeEvents(lastEventId: number) {
    return this.request(`/v1/poll/ack/${lastEventId}`, {
      method: 'POST'
    });
  }

  async resetPollState(eventId: number) {
    return this.request(`/v1/poll/reset/${eventId}`, {
      method: 'POST'
    });
  }
}

// Create default clients (can work without API key for public queries)
export const graphqlClient = createGraphQLClient();
export const domaAPIClient = new DomaAPIClient();

// Create authenticated clients when API key is available
export const createAuthenticatedClients = (apiKey: string) => ({
  graphqlClient: createGraphQLClient(apiKey),
  apiClient: new DomaAPIClient(apiKey)
});

export { DOMA_CONFIG };
