import React, { useState, useMemo } from 'react';
import { Link } from 'react-router-dom';
import { Search, Filter, ExternalLink, Heart, MessageCircle, TrendingUp, Clock, Shield, Eye, Zap } from 'lucide-react';
import SEOHead from '../components/SEOHead';
import { useDomains, useMarketplaceListings } from '../hooks/useDomainData';
import { formatPrice, formatAddress, formatTimeAgo, getDomainCategory, isExpired, getTimeRemaining } from '../utils/formatters';

interface Domain {
  id: string;
  name: string;
  price: string;
  currency: string;
  description: string;
  category: string;
  views: number;
  likes: number;
  isVerified: boolean;
  image?: string;
  tokenId?: string;
  ownerAddress?: string;
  expiresAt?: string;
  orderbook?: string;
  orderId?: string;
  isListed?: boolean;
  timeRemaining?: string;
}

const EnhancedMarketplace: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortBy, setSortBy] = useState('popularity');
  const [showFilters, setShowFilters] = useState(false);
  const [page, setPage] = useState(0);
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });
  const [statusFilter, setStatusFilter] = useState('all'); // all, listed, unlisted, expiring

  // Fetch real domains from Doma API
  const { 
    data: domaDomainsData, 
    loading: domaLoading, 
    error: domaError 
  } = useDomains({
    skip: page * 20,
    take: 20,
    claimStatus: 'ALL',
    tlds: ['doma'],
    sortOrder: 'DESC'
  });

  // Fetch marketplace listings
  const { 
    data: listingsData, 
    loading: listingsLoading, 
    error: listingsError 
  } = useMarketplaceListings({
    skip: page * 20,
    take: 20,
    tlds: ['doma'],
    networkIds: ['eip155:97476'] // Doma testnet
  });

  // Convert Doma API data to our Domain interface
  const domains: Domain[] = useMemo(() => {
    // If we have real data, use it
    if (domaDomainsData?.items && domaDomainsData.items.length > 0) {
      return domaDomainsData.items.map((domainData: any, index: number) => {
        const token = domainData.tokens?.[0];
        const listing = token?.listings?.[0];
        const hasActiveListing = listing && !isExpired(listing.expiresAt);

        return {
          id: token?.tokenId || `${index}`,
          name: domainData.name,
          price: hasActiveListing ? formatPrice(listing.price, listing.currency.symbol, listing.currency.decimals).split(' ')[0] : 'Not Listed',
          currency: listing?.currency?.symbol || 'ETH',
          description: `Premium ${getDomainCategory(domainData.name).toLowerCase()} domain on Doma Protocol`,
          category: getDomainCategory(domainData.name),
          views: Math.floor(Math.random() * 2000) + 500, // Mock for now - would come from analytics
          likes: Math.floor(Math.random() * 150) + 20, // Mock for now - would come from user interactions
          isVerified: !domainData.eoi && !!domainData.claimedBy,
          tokenId: token?.tokenId,
          ownerAddress: token?.ownerAddress,
          expiresAt: domainData.expiresAt,
          orderbook: listing?.orderbook,
          orderId: listing?.id,
          isListed: hasActiveListing,
          timeRemaining: getTimeRemaining(domainData.expiresAt)
        };
      });
    }

    // Fallback to listings data if domains data isn't available
    if (listingsData?.items && listingsData.items.length > 0) {
      return listingsData.items.map((listing: any) => ({
        id: listing.tokenId,
        name: listing.name,
        price: formatPrice(listing.price, listing.currency.symbol, listing.currency.decimals).split(' ')[0],
        currency: listing.currency.symbol,
        description: `Premium ${getDomainCategory(listing.name).toLowerCase()} domain on Doma Protocol`,
        category: getDomainCategory(listing.name),
        views: Math.floor(Math.random() * 2000) + 500,
        likes: Math.floor(Math.random() * 150) + 20,
        isVerified: true,
        tokenId: listing.tokenId,
        ownerAddress: listing.offererAddress,
        expiresAt: listing.expiresAt,
        orderbook: listing.orderbook,
        orderId: listing.id,
        isListed: true,
        timeRemaining: getTimeRemaining(listing.expiresAt)
      }));
    }

    // Fallback to mock data when no real data is available (for demo purposes)
    return [
      {
        id: '1',
        name: 'crypto.doma',
        price: '5.5',
        currency: 'ETH',
        description: 'Premium crypto domain perfect for DeFi projects',
        category: 'Finance',
        views: 1250,
        likes: 89,
        isVerified: true,
        isListed: true,
        timeRemaining: '45 days'
      },
      {
        id: '2',
        name: 'nft.doma',
        price: '3.2',
        currency: 'ETH',
        description: 'Ideal for NFT marketplaces and collections',
        category: 'NFT',
        views: 980,
        likes: 67,
        isVerified: true,
        isListed: true,
        timeRemaining: '128 days'
      },
      {
        id: '3',
        name: 'gaming.doma',
        price: 'Not Listed',
        currency: 'ETH',
        description: 'Perfect domain for Web3 gaming platforms',
        category: 'Gaming',
        views: 756,
        likes: 45,
        isVerified: true,
        isListed: false,
        timeRemaining: '89 days'
      },
      {
        id: '4',
        name: 'defi.doma',
        price: '6.1',
        currency: 'ETH',
        description: 'High-value DeFi domain for financial applications',
        category: 'Finance',
        views: 1450,
        likes: 112,
        isVerified: true,
        isListed: true,
        timeRemaining: '234 days'
      },
      {
        id: '5',
        name: 'web3.doma',
        price: '4.6',
        currency: 'ETH',
        description: 'Essential Web3 domain for blockchain projects',
        category: 'Technology',
        views: 1120,
        likes: 78,
        isVerified: true,
        isListed: true,
        timeRemaining: '156 days'
      },
      {
        id: '6',
        name: 'art.doma',
        price: 'Not Listed',
        currency: 'ETH',
        description: 'Perfect for digital art and creative platforms',
        category: 'NFT',
        views: 894,
        likes: 56,
        isVerified: false,
        isListed: false,
        timeRemaining: '67 days'
      },
    ];
  }, [domaDomainsData, listingsData]);

  const isLoading = domaLoading || listingsLoading;
  const hasError = domaError || listingsError;

  const categories = ['All', 'Finance', 'NFT', 'Gaming', 'Technology', 'Art'];
  const sortOptions = [
    { value: 'popularity', label: 'Most Popular' },
    { value: 'price-low', label: 'Price: Low to High' },
    { value: 'price-high', label: 'Price: High to Low' },
    { value: 'newest', label: 'Recently Listed' },
    { value: 'expiring', label: 'Expiring Soon' },
  ];

  const filteredDomains = domains.filter(domain => {
    const matchesSearch = domain.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         domain.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'All' || domain.category === selectedCategory;

    // Price range filter
    const price = domain.price === 'Not Listed' ? 0 : parseFloat(domain.price);
    const matchesPriceMin = priceRange.min === '' || price >= parseFloat(priceRange.min);
    const matchesPriceMax = priceRange.max === '' || price <= parseFloat(priceRange.max);

    // Status filter
    let matchesStatus = true;
    if (statusFilter === 'listed') {
      matchesStatus = domain.isListed === true;
    } else if (statusFilter === 'unlisted') {
      matchesStatus = domain.isListed === false;
    } else if (statusFilter === 'expiring') {
      // Domains expiring in the next 30 days
      matchesStatus = Boolean(domain.timeRemaining && 
        domain.timeRemaining.includes('day') && 
        parseInt(domain.timeRemaining) <= 30);
    }

    return matchesSearch && matchesCategory && matchesPriceMin && matchesPriceMax && matchesStatus;
  });

  const sortedDomains = [...filteredDomains].sort((a, b) => {
    switch (sortBy) {
      case 'price-low':
        const priceA = a.price === 'Not Listed' ? Infinity : parseFloat(a.price);
        const priceB = b.price === 'Not Listed' ? Infinity : parseFloat(b.price);
        return priceA - priceB;
      case 'price-high':
        const priceAHigh = a.price === 'Not Listed' ? 0 : parseFloat(a.price);
        const priceBHigh = b.price === 'Not Listed' ? 0 : parseFloat(b.price);
        return priceBHigh - priceAHigh;
      case 'popularity':
        return b.views - a.views;
      case 'newest':
        // Would use actual listing date in real implementation
        return b.likes - a.likes; // Mock sorting by likes for now
      case 'expiring':
        // Sort by time remaining (ascending)
        const getNumericTime = (timeStr?: string) => {
          if (!timeStr) return Infinity;
          const match = timeStr.match(/(\d+)/);
          return match ? parseInt(match[1]) : Infinity;
        };
        return getNumericTime(a.timeRemaining) - getNumericTime(b.timeRemaining);
      default:
        return 0;
    }
  });

  // Stats for the marketplace
  const stats = {
    totalDomains: domains.length,
    listedDomains: domains.filter(d => d.isListed).length,
    totalVolume: domains.reduce((sum, d) => sum + (d.price !== 'Not Listed' ? parseFloat(d.price) : 0), 0).toFixed(1),
    avgPrice: (domains.filter(d => d.isListed).reduce((sum, d) => sum + parseFloat(d.price), 0) / domains.filter(d => d.isListed).length || 0).toFixed(2)
  };

  return (
    <>
      <SEOHead 
        title="Doma Marketplace - Trade Premium Domains"
        description="Discover and trade premium .doma domains on the decentralized marketplace. Find perfect domains for your Web3 projects with verified ownership and secure transactions."
        keywords="doma domains, domain marketplace, web3 domains, blockchain domains, crypto domains"
      />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">
            Doma Marketplace
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Discover, trade, and invest in premium .doma domains. The decentralized marketplace for Web3 domain ownership.
          </p>
        </div>

        {/* Stats Bar */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
          <div className="bg-white rounded-lg border border-gray-200 p-4 text-center">
            <div className="text-2xl font-bold text-blue-600">{stats.totalDomains}</div>
            <div className="text-sm text-gray-600">Total Domains</div>
          </div>
          <div className="bg-white rounded-lg border border-gray-200 p-4 text-center">
            <div className="text-2xl font-bold text-green-600">{stats.listedDomains}</div>
            <div className="text-sm text-gray-600">Listed for Sale</div>
          </div>
          <div className="bg-white rounded-lg border border-gray-200 p-4 text-center">
            <div className="text-2xl font-bold text-purple-600">{stats.totalVolume} ETH</div>
            <div className="text-sm text-gray-600">Total Value</div>
          </div>
          <div className="bg-white rounded-lg border border-gray-200 p-4 text-center">
            <div className="text-2xl font-bold text-orange-600">{stats.avgPrice} ETH</div>
            <div className="text-sm text-gray-600">Avg. Price</div>
          </div>
        </div>

        {/* Search and Filters */}
        <div className="bg-white rounded-lg border border-gray-200 p-6 mb-8">
          <div className="flex flex-col lg:flex-row gap-4">
            {/* Search */}
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Search domains..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              />
            </div>

            {/* Quick Filters */}
            <div className="flex gap-2 flex-wrap">
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              >
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>

              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              >
                {sortOptions.map(option => (
                  <option key={option.value} value={option.value}>{option.label}</option>
                ))}
              </select>

              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              >
                <option value="all">All Status</option>
                <option value="listed">Listed</option>
                <option value="unlisted">Not Listed</option>
                <option value="expiring">Expiring Soon</option>
              </select>

              <button
                onClick={() => setShowFilters(!showFilters)}
                className="flex items-center gap-2 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
              >
                <Filter className="w-4 h-4" />
                Filters
              </button>
            </div>
          </div>

          {/* Advanced Filters */}
          {showFilters && (
            <div className="mt-4 pt-4 border-t border-gray-200">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Min Price (ETH)</label>
                  <input
                    type="number"
                    placeholder="0"
                    value={priceRange.min}
                    onChange={(e) => setPriceRange(prev => ({ ...prev, min: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Max Price (ETH)</label>
                  <input
                    type="number"
                    placeholder="100"
                    value={priceRange.max}
                    onChange={(e) => setPriceRange(prev => ({ ...prev, max: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  />
                </div>
                <div className="md:col-span-2 flex items-end">
                  <button
                    onClick={() => {
                      setPriceRange({ min: '', max: '' });
                      setStatusFilter('all');
                    }}
                    className="px-4 py-2 text-gray-600 hover:text-gray-800 transition-colors"
                  >
                    Clear Filters
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Error State */}
        {hasError && (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 mb-6">
            <div className="text-yellow-800">
              <p className="font-medium">Unable to fetch live data from Doma Protocol</p>
              <p className="text-sm mt-1">Showing sample data for demonstration. In production, this would display real domain listings from the Doma subgraph.</p>
            </div>
          </div>
        )}

        {/* Loading State */}
        {isLoading && (
          <div className="text-center py-12">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
            <p className="mt-2 text-gray-600">Loading domains from Doma Protocol...</p>
          </div>
        )}

        {/* Results */}
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-lg font-semibold text-gray-900">
            {sortedDomains.length} Domain{sortedDomains.length !== 1 ? 's' : ''} Found
          </h2>
          {sortedDomains.length > 0 && (
            <div className="flex gap-2">
              <button
                disabled={page === 0}
                onClick={() => setPage(page - 1)}
                className="px-3 py-1 border border-gray-300 rounded text-sm disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>
              <span className="px-3 py-1 text-sm text-gray-600">Page {page + 1}</span>
              <button
                onClick={() => setPage(page + 1)}
                className="px-3 py-1 border border-gray-300 rounded text-sm"
              >
                Next
              </button>
            </div>
          )}
        </div>

        {/* Domain Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {sortedDomains.map((domain) => (
            <DomainCard key={domain.id} domain={domain} />
          ))}
        </div>

        {/* Empty State */}
        {sortedDomains.length === 0 && !isLoading && (
          <div className="text-center py-12">
            <div className="w-16 h-16 mx-auto mb-4 bg-gray-100 rounded-full flex items-center justify-center">
              <Search className="w-8 h-8 text-gray-400" />
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No domains found</h3>
            <p className="text-gray-600">Try adjusting your search terms or filters</p>
          </div>
        )}
      </div>
    </>
  );
};

