import React, { useState } from 'react';
import { 
  Search, 
  Send, 
  MoreVertical, 
  Phone, 
  Video, 
  Paperclip,
  Smile,
  ExternalLink,
  Shield,
  Clock,
  CheckCircle,
  AlertCircle,
  DollarSign,
  Gavel,
  FileText,
  Link as LinkIcon
} from 'lucide-react';

interface TradeMessage {
  id: string;
  content: string;
  timestamp: string;
  sender: 'user' | 'contact';
  type: 'text' | 'offer' | 'counteroffer' | 'contract' | 'escrow' | 'domain-link';
  metadata?: {
    domainName?: string;
    offerAmount?: string;
    currency?: string;
    expiryTime?: string;
    contractAddress?: string;
    escrowId?: string;
    status?: 'pending' | 'accepted' | 'rejected' | 'expired';
  };
}

interface TradeContact {
  id: string;
  name: string;
  address: string;
  avatar?: string;
  lastMessage: string;
  timestamp: string;
  unread: number;
  online: boolean;
  domainContext: string;
  tradeStatus: 'negotiating' | 'offer-pending' | 'in-escrow' | 'completed' | 'cancelled';
  verificationLevel: 'basic' | 'verified' | 'premium';
}

const TradeMessaging: React.FC = () => {
  const [selectedContact, setSelectedContact] = useState<TradeContact | null>(null);
  const [messages, setMessages] = useState<TradeMessage[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showOfferForm, setShowOfferForm] = useState(false);
  const [offerAmount, setOfferAmount] = useState('');
  const [offerCurrency, setOfferCurrency] = useState('ETH');
  const [offerExpiry, setOfferExpiry] = useState('24');

  // Mock contacts with trade-specific data
  const tradeContacts: TradeContact[] = [
    {
      id: '1',
      name: 'CryptoInvestor.eth',
      address: '0x1234...5678',
      lastMessage: 'I can do 5.2 ETH for crypto.doma. Final offer.',
      timestamp: '2 min ago',
      unread: 2,
      online: true,
      domainContext: 'crypto.doma',
      tradeStatus: 'negotiating',
      verificationLevel: 'verified'
    },
    {
      id: '2',
      name: 'DomainTrader',
      address: '0xabcd...efgh',
      lastMessage: 'Escrow has been initiated for nft.doma',
      timestamp: '1 hour ago',
      unread: 0,
      online: false,
      domainContext: 'nft.doma',
      tradeStatus: 'in-escrow',
      verificationLevel: 'premium'
    },
    {
      id: '3',
      name: 'Web3Builder',
      address: '0x9876...5432',
      lastMessage: 'Thanks for the smooth transaction!',
      timestamp: '3 hours ago',
      unread: 0,
      online: true,
      domainContext: 'defi.doma',
      tradeStatus: 'completed',
      verificationLevel: 'basic'
    },
  ];

  // Mock messages for selected contact
  const mockMessages: TradeMessage[] = [
    {
      id: '1',
      content: 'Hi! I\'m interested in purchasing crypto.doma. Is it still available?',
      timestamp: '10:30 AM',
      sender: 'contact',
      type: 'text'
    },
    {
      id: '2',
      content: 'Yes, it\'s available! I\'m asking 5.5 ETH for it.',
      timestamp: '10:32 AM',
      sender: 'user',
      type: 'text'
    },
    {
      id: '3',
      content: 'I\'d like to make an offer',
      timestamp: '10:35 AM',
      sender: 'contact',
      type: 'offer',
      metadata: {
        domainName: 'crypto.doma',
        offerAmount: '4.8',
        currency: 'ETH',
        expiryTime: '24 hours',
        status: 'pending'
      }
    },
    {
      id: '4',
      content: 'I can meet you at 5.2 ETH. That\'s my final offer.',
      timestamp: '10:40 AM',
      sender: 'contact',
      type: 'counteroffer',
      metadata: {
        domainName: 'crypto.doma',
        offerAmount: '5.2',
        currency: 'ETH',
        expiryTime: '12 hours',
        status: 'pending'
      }
    },
    {
      id: '5',
      content: 'Deal! Let\'s use escrow for security.',
      timestamp: '10:45 AM',
      sender: 'user',
      type: 'text'
    }
  ];

  React.useEffect(() => {
    if (selectedContact) {
      setMessages(mockMessages);
    }
  }, [selectedContact]);

  const sendMessage = () => {
    if (newMessage.trim() && selectedContact) {
      const message: TradeMessage = {
        id: (messages.length + 1).toString(),
        content: newMessage,
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        sender: 'user',
        type: 'text'
      };
      setMessages([...messages, message]);
      setNewMessage('');
    }
  };

  const sendOffer = () => {
    if (offerAmount && selectedContact) {
      const offerMessage: TradeMessage = {
        id: (messages.length + 1).toString(),
        content: `Formal offer for ${selectedContact.domainContext}`,
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        sender: 'user',
        type: 'offer',
        metadata: {
          domainName: selectedContact.domainContext,
          offerAmount,
          currency: offerCurrency,
          expiryTime: `${offerExpiry} hours`,
          status: 'pending'
        }
      };
      setMessages([...messages, offerMessage]);
      setShowOfferForm(false);
      setOfferAmount('');
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'negotiating': return 'bg-yellow-100 text-yellow-800';
      case 'offer-pending': return 'bg-blue-100 text-blue-800';
      case 'in-escrow': return 'bg-purple-100 text-purple-800';
      case 'completed': return 'bg-green-100 text-green-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getVerificationIcon = (level: string) => {
    switch (level) {
      case 'premium': return <Shield className="w-4 h-4 text-purple-500" />;
      case 'verified': return <CheckCircle className="w-4 h-4 text-green-500" />;
      default: return <AlertCircle className="w-4 h-4 text-gray-400" />;
    }
  };

  const filteredContacts = tradeContacts.filter(contact =>
    contact.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    contact.domainContext.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="h-[calc(100vh-180px)] bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
      <div className="flex h-full">
        {/* Contacts Sidebar */}
        <div className="w-1/3 border-r border-gray-200 flex flex-col">
          {/* Header */}
          <div className="p-4 border-b border-gray-200">
            <h2 className="text-lg font-semibold text-gray-900 mb-3">Trade Negotiations</h2>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search trades..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-9 pr-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              />
            </div>
          </div>

          {/* Trade Status Filters */}
          <div className="p-4 border-b border-gray-200">
            <div className="flex flex-wrap gap-2">
              {['all', 'negotiating', 'in-escrow', 'completed'].map((status) => (
                <button
                  key={status}
                  className="px-3 py-1 text-xs font-medium bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-full transition-colors"
                >
                  {status.charAt(0).toUpperCase() + status.slice(1)}
                </button>
              ))}
            </div>
          </div>

          {/* Contacts List */}
          <div className="flex-1 overflow-y-auto">
            {filteredContacts.map((contact) => (
              <div
                key={contact.id}
                onClick={() => setSelectedContact(contact)}
                className={`p-4 border-b border-gray-100 cursor-pointer hover:bg-gray-50 transition-colors ${
                  selectedContact?.id === contact.id ? 'bg-primary-50 border-primary-200' : ''
                }`}
              >
                <div className="flex items-start gap-3">
                  <div className="relative">
                    <div className="w-12 h-12 bg-gradient-to-br from-primary-400 to-secondary-400 rounded-full flex items-center justify-center text-white font-semibold">
                      {contact.name.charAt(0).toUpperCase()}
                    </div>
                    {contact.online && (
                      <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 border-2 border-white rounded-full"></div>
                    )}
                    <div className="absolute -top-1 -right-1">
                      {getVerificationIcon(contact.verificationLevel)}
                    </div>
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between">
                      <h3 className="font-medium text-gray-900 truncate">{contact.name}</h3>
                      <span className="text-xs text-gray-500">{contact.timestamp}</span>
                    </div>
                    <div className="flex items-center justify-between mt-1">
                      <p className="text-sm text-gray-600 truncate">{contact.lastMessage}</p>
                      {contact.unread > 0 && (
                        <span className="ml-2 bg-primary-600 text-white text-xs font-medium px-2 py-1 rounded-full min-w-[20px] text-center">
                          {contact.unread}
                        </span>
                      )}
                    </div>
                    <div className="mt-2 flex items-center gap-2">
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                        {contact.domainContext}
                      </span>
                      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(contact.tradeStatus)}`}>
                        {contact.tradeStatus.replace('-', ' ')}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Chat Area */}
        <div className="flex-1 flex flex-col">
          {selectedContact ? (
            <>
              {/* Chat Header */}
              <div className="p-4 border-b border-gray-200 flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <div className="relative">
                    <div className="w-10 h-10 bg-gradient-to-br from-primary-400 to-secondary-400 rounded-full flex items-center justify-center text-white font-semibold">
                      {selectedContact.name.charAt(0).toUpperCase()}
                    </div>
                    {selectedContact.online && (
                      <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-500 border-2 border-white rounded-full"></div>
                    )}
                  </div>
                  <div>
                    <h3 className="font-medium text-gray-900 flex items-center gap-2">
                      {selectedContact.name}
                      {getVerificationIcon(selectedContact.verificationLevel)}
                    </h3>
                    <p className="text-sm text-gray-600">Trading: {selectedContact.domainContext}</p>
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  <button className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors">
                    <Phone className="w-5 h-5" />
                  </button>
                  <button className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors">
                    <Video className="w-5 h-5" />
                  </button>
                  <button className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors">
                    <ExternalLink className="w-5 h-5" />
                  </button>
                  <button className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors">
                    <MoreVertical className="w-5 h-5" />
                  </button>
                </div>
              </div>

              {/* Trade Status Banner */}
              <div className={`px-4 py-2 border-b border-gray-200 ${getStatusColor(selectedContact.tradeStatus)}`}>
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium">
                    Trade Status: {selectedContact.tradeStatus.replace('-', ' ').toUpperCase()}
                  </span>
                  {selectedContact.tradeStatus === 'in-escrow' && (
                    <button className="text-sm underline">View Escrow Details</button>
                  )}
                </div>
              </div>

              {/* Messages */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div className="max-w-xs lg:max-w-md">
                      {message.type === 'text' ? (
                        <div
                          className={`px-4 py-2 rounded-lg ${
                            message.sender === 'user'
                              ? 'bg-primary-600 text-white'
                              : 'bg-gray-100 text-gray-900'
                          }`}
                        >
                          <p>{message.content}</p>
                          <div
                            className={`text-xs mt-1 ${
                              message.sender === 'user' ? 'text-primary-100' : 'text-gray-500'
                            }`}
                          >
                            {message.timestamp}
                          </div>
                        </div>
                      ) : (
                        <div className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm">
                          <div className="flex items-start gap-3">
                            <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                              message.type === 'offer' ? 'bg-blue-100' :
                              message.type === 'counteroffer' ? 'bg-purple-100' :
                              message.type === 'contract' ? 'bg-green-100' : 'bg-gray-100'
                            }`}>
                              {message.type === 'offer' && <DollarSign className="w-4 h-4 text-blue-600" />}
                              {message.type === 'counteroffer' && <Gavel className="w-4 h-4 text-purple-600" />}
                              {message.type === 'contract' && <FileText className="w-4 h-4 text-green-600" />}
                              {message.type === 'domain-link' && <LinkIcon className="w-4 h-4 text-gray-600" />}
                            </div>
                            <div className="flex-1">
                              <div className="flex items-center gap-2 mb-2">
                                <span className="font-medium text-gray-900">
                                  {message.type === 'offer' ? 'Offer' : 
                                   message.type === 'counteroffer' ? 'Counter Offer' :
                                   message.type === 'contract' ? 'Smart Contract' : 'Domain Link'}
                                </span>
                                <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                                  message.metadata?.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                                  message.metadata?.status === 'accepted' ? 'bg-green-100 text-green-800' :
                                  message.metadata?.status === 'rejected' ? 'bg-red-100 text-red-800' :
                                  'bg-gray-100 text-gray-800'
                                }`}>
                                  {message.metadata?.status || 'active'}
                                </span>
                              </div>

                              {message.metadata && (
                                <div className="space-y-1 text-sm">
                                  <div><strong>Domain:</strong> {message.metadata.domainName}</div>
                                  <div><strong>Amount:</strong> {message.metadata.offerAmount} {message.metadata.currency}</div>
                                  {message.metadata.expiryTime && (
                                    <div className="flex items-center gap-1 text-gray-600">
                                      <Clock className="w-3 h-3" />
                                      Expires in {message.metadata.expiryTime}
                                    </div>
                                  )}
                                </div>
                              )}

                              <div className="flex gap-2 mt-3">
                                {message.sender === 'contact' && message.metadata?.status === 'pending' && (
                                  <>
                                    <button className="px-3 py-1 bg-green-600 hover:bg-green-700 text-white text-xs rounded-md transition-colors">
                                      Accept
                                    </button>
                                    <button className="px-3 py-1 bg-red-600 hover:bg-red-700 text-white text-xs rounded-md transition-colors">
                                      Reject
                                    </button>
                                    <button className="px-3 py-1 bg-purple-600 hover:bg-purple-700 text-white text-xs rounded-md transition-colors">
                                      Counter
                                    </button>
                                  </>
                                )}
                              </div>
                            </div>
                          </div>
                          <div className="text-xs text-gray-500 mt-2">{message.timestamp}</div>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>

              {/* Trade Actions Bar */}
              <div className="px-4 py-2 border-t border-gray-200 bg-gray-50">
                <div className="flex gap-2">
                  <button
                    onClick={() => setShowOfferForm(true)}
                    className="px-3 py-1 bg-blue-600 hover:bg-blue-700 text-white text-sm rounded-md transition-colors flex items-center gap-1"
                  >
                    <DollarSign className="w-3 h-3" />
                    Make Offer
                  </button>
                  <button className="px-3 py-1 bg-purple-600 hover:bg-purple-700 text-white text-sm rounded-md transition-colors flex items-center gap-1">
                    <Shield className="w-3 h-3" />
                    Escrow
                  </button>
                  <button className="px-3 py-1 bg-green-600 hover:bg-green-700 text-white text-sm rounded-md transition-colors flex items-center gap-1">
                    <FileText className="w-3 h-3" />
                    Contract
                  </button>
                </div>
              </div>

              {/* Message Input */}
              <div className="p-4 border-t border-gray-200">
                <div className="flex items-end gap-2">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-2">
                      <button className="p-1 text-gray-500 hover:text-gray-700 transition-colors">
                        <Paperclip className="w-4 h-4" />
                      </button>
                      <button className="p-1 text-gray-500 hover:text-gray-700 transition-colors">
                        <Smile className="w-4 h-4" />
                      </button>
                    </div>
                    <div className="relative">
                      <textarea
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        onKeyPress={(e) => {
                          if (e.key === 'Enter' && !e.shiftKey) {
                            e.preventDefault();
                            sendMessage();
                          }
                        }}
                        placeholder="Type a message..."
                        rows={2}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                      />
                    </div>
                  </div>
                  <button
                    onClick={sendMessage}
                    disabled={!newMessage.trim()}
                    className="p-2 bg-primary-600 hover:bg-primary-700 disabled:bg-gray-300 text-white rounded-lg transition-colors"
                  >
                    <Send className="w-5 h-5" />
                  </button>
                </div>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center text-gray-500">
                <div className="w-16 h-16 mx-auto mb-4 bg-gray-200 rounded-full flex items-center justify-center">
                  <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                  </svg>
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">Select a trade conversation</h3>
                <p>Choose a negotiation from the sidebar to start messaging</p>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Offer Form Modal */}
      {showOfferForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Make an Offer</h3>
            <form className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Domain</label>
                <input 
                  type="text"
                  value={selectedContact?.domainContext || ''}
                  disabled
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg bg-gray-50"
                />
              </div>
              <div className="grid grid-cols-2 gap-3">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Amount</label>
                  <input 
                    type="number"
                    placeholder="5.0"
                    value={offerAmount}
                    onChange={(e) => setOfferAmount(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Currency</label>
                  <select 
                    value={offerCurrency}
                    onChange={(e) => setOfferCurrency(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  >
                    <option value="ETH">ETH</option>
                    <option value="USDC">USDC</option>
                    <option value="DAI">DAI</option>
                  </select>
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Offer Expires In</label>
                <select 
                  value={offerExpiry}
                  onChange={(e) => setOfferExpiry(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                >
                  <option value="1">1 hour</option>
                  <option value="6">6 hours</option>
                  <option value="24">24 hours</option>
                  <option value="72">3 days</option>
                  <option value="168">1 week</option>
                </select>
              </div>
              <div className="flex gap-3">
                <button 
                  type="button"
                  onClick={() => setShowOfferForm(false)}
                  className="flex-1 py-2 border border-gray-300 text-gray-700 hover:bg-gray-50 rounded-lg transition-colors"
                >
                  Cancel
                </button>
                <button 
                  type="button"
                  onClick={sendOffer}
                  className="flex-1 py-2 bg-primary-600 hover:bg-primary-700 text-white rounded-lg transition-colors"
                >
                  Send Offer
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default TradeMessaging;
