import React, { useState } from 'react';
import { 
  Search, 
  Send, 
  MoreVertical, 
  Phone, 
  Video, 
  Paperclip,
  Smile,
  Star,
  Archive,
  Trash2
} from 'lucide-react';

interface Message {
  id: string;
  content: string;
  timestamp: string;
  sender: 'user' | 'contact';
  type: 'text' | 'offer' | 'domain';
  metadata?: {
    domainName?: string;
    offerAmount?: string;
    currency?: string;
  };
}

interface Contact {
  id: string;
  name: string;
  avatar?: string;
  address: string;
  lastMessage: string;
  timestamp: string;
  unread: number;
  online: boolean;
  domainContext?: string;
}

const mockContacts: Contact[] = [
  {
    id: '1',
    name: 'CryptoTrader.eth',
    address: '0x1234...5678',
    lastMessage: 'Interested in crypto.doma. Can we negotiate?',
    timestamp: '2 min ago',
    unread: 2,
    online: true,
    domainContext: 'crypto.doma'
  },
  {
    id: '2',
    name: 'NFTCollector',
    address: '0xabcd...efgh',
    lastMessage: 'Thanks for the domain transfer!',
    timestamp: '1 hour ago',
    unread: 0,
    online: false,
    domainContext: 'art.doma'
  },
  {
    id: '3',
    name: 'DeFiBuilder',
    address: '0x9876...5432',
    lastMessage: 'I have a counter-offer for defi.doma',
    timestamp: '3 hours ago',
    unread: 1,
    online: true,
    domainContext: 'defi.doma'
  },
];

const mockMessages: Message[] = [
  {
    id: '1',
    content: 'Hi! I\'m interested in purchasing crypto.doma. Is it still available?',
    timestamp: '10:30 AM',
    sender: 'contact',
    type: 'text'
  },
  {
    id: '2',
    content: 'Yes, it\'s still available! The current asking price is 5.5 ETH.',
    timestamp: '10:32 AM',
    sender: 'user',
    type: 'text'
  },
  {
    id: '3',
    content: 'I\'d like to make an offer of 4.8 ETH. Would you consider it?',
    timestamp: '10:35 AM',
    sender: 'contact',
    type: 'offer',
    metadata: {
      domainName: 'crypto.doma',
      offerAmount: '4.8',
      currency: 'ETH'
    }
  },
  {
    id: '4',
    content: 'That\'s a bit low for me. How about we meet in the middle at 5.2 ETH?',
    timestamp: '10:40 AM',
    sender: 'user',
    type: 'text'
  },
  {
    id: '5',
    content: 'That works for me! Let\'s proceed with the transaction.',
    timestamp: '10:45 AM',
    sender: 'contact',
    type: 'text'
  }
];

const Messages: React.FC = () => {
  const [selectedContact, setSelectedContact] = useState<Contact | null>(mockContacts[0]);
  const [messages, setMessages] = useState<Message[]>(mockMessages);
  const [newMessage, setNewMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const filteredContacts = mockContacts.filter(contact =>
    contact.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    contact.address.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (contact.domainContext && contact.domainContext.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const sendMessage = () => {
    if (newMessage.trim() && selectedContact) {
      const message: Message = {
        id: (messages.length + 1).toString(),
        content: newMessage,
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        sender: 'user',
        type: 'text'
      };
      setMessages([...messages, message]);
      setNewMessage('');
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="h-[calc(100vh-180px)] bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
      <div className="flex h-full">
        {/* Contacts Sidebar */}
        <div className="w-1/3 border-r border-gray-200 flex flex-col">
          {/* Header */}
          <div className="p-4 border-b border-gray-200">
            <h2 className="text-lg font-semibold text-gray-900 mb-3">Messages</h2>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search conversations..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-9 pr-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              />
            </div>
          </div>

          {/* Contacts List */}
          <div className="flex-1 overflow-y-auto">
            {filteredContacts.map((contact) => (
              <div
                key={contact.id}
                onClick={() => setSelectedContact(contact)}
                className={`p-4 border-b border-gray-100 cursor-pointer hover:bg-gray-50 transition-colors ${
                  selectedContact?.id === contact.id ? 'bg-primary-50 border-primary-200' : ''
                }`}
              >
                <div className="flex items-center gap-3">
                  <div className="relative">
                    <div className="w-12 h-12 bg-gradient-to-br from-primary-400 to-secondary-400 rounded-full flex items-center justify-center text-white font-semibold">
                      {contact.name.charAt(0).toUpperCase()}
                    </div>
                    {contact.online && (
                      <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 border-2 border-white rounded-full"></div>
                    )}
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between">
                      <h3 className="font-medium text-gray-900 truncate">{contact.name}</h3>
                      <span className="text-xs text-gray-500">{contact.timestamp}</span>
                    </div>
                    <div className="flex items-center justify-between">
                      <p className="text-sm text-gray-600 truncate">{contact.lastMessage}</p>
                      {contact.unread > 0 && (
                        <span className="ml-2 bg-primary-600 text-white text-xs font-medium px-2 py-1 rounded-full min-w-[20px] text-center">
                          {contact.unread}
                        </span>
                      )}
                    </div>
                    {contact.domainContext && (
                      <div className="mt-1">
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                          Re: {contact.domainContext}
                        </span>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Chat Area */}
        <div className="flex-1 flex flex-col">
          {selectedContact ? (
            <>
              {/* Chat Header */}
              <div className="p-4 border-b border-gray-200 flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <div className="relative">
                    <div className="w-10 h-10 bg-gradient-to-br from-primary-400 to-secondary-400 rounded-full flex items-center justify-center text-white font-semibold">
                      {selectedContact.name.charAt(0).toUpperCase()}
                    </div>
                    {selectedContact.online && (
                      <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-500 border-2 border-white rounded-full"></div>
                    )}
                  </div>
                  <div>
                    <h3 className="font-medium text-gray-900">{selectedContact.name}</h3>
                    <p className="text-sm text-gray-600">{selectedContact.address}</p>
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  <button className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors">
                    <Phone className="w-5 h-5" />
                  </button>
                  <button className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors">
                    <Video className="w-5 h-5" />
                  </button>
                  <button className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors">
                    <MoreVertical className="w-5 h-5" />
                  </button>
                </div>
              </div>

              {/* Messages */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div
                      className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                        message.sender === 'user'
                          ? 'bg-primary-600 text-white'
                          : 'bg-gray-100 text-gray-900'
                      }`}
                    >
                      {message.type === 'offer' && message.metadata ? (
                        <div className="space-y-2">
                          <div className="text-sm font-medium">
                            Offer for {message.metadata.domainName}
                          </div>
                          <div className="text-lg font-bold">
                            {message.metadata.offerAmount} {message.metadata.currency}
                          </div>
                          <div className="text-sm opacity-90">{message.content}</div>
                          {message.sender === 'contact' && (
                            <div className="flex gap-2 mt-2">
                              <button className="bg-white bg-opacity-20 hover:bg-opacity-30 px-3 py-1 rounded text-sm transition-colors">
                                Accept
                              </button>
                              <button className="bg-white bg-opacity-20 hover:bg-opacity-30 px-3 py-1 rounded text-sm transition-colors">
                                Counter
                              </button>
                            </div>
                          )}
                        </div>
                      ) : (
                        <p>{message.content}</p>
                      )}
                      <div
                        className={`text-xs mt-1 ${
                          message.sender === 'user' ? 'text-primary-100' : 'text-gray-500'
                        }`}
                      >
                        {message.timestamp}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Message Input */}
              <div className="p-4 border-t border-gray-200">
                <div className="flex items-end gap-2">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-2">
                      <button className="p-1 text-gray-500 hover:text-gray-700 transition-colors">
                        <Paperclip className="w-4 h-4" />
                      </button>
                      <button className="p-1 text-gray-500 hover:text-gray-700 transition-colors">
                        <Smile className="w-4 h-4" />
                      </button>
                    </div>
                    <div className="relative">
                      <textarea
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder="Type a message..."
                        rows={2}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                      />
                    </div>
                  </div>
                  <button
                    onClick={sendMessage}
                    disabled={!newMessage.trim()}
                    className="p-2 bg-primary-600 hover:bg-primary-700 disabled:bg-gray-300 text-white rounded-lg transition-colors"
                  >
                    <Send className="w-5 h-5" />
                  </button>
                </div>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center text-gray-500">
                <div className="w-16 h-16 mx-auto mb-4 bg-gray-200 rounded-full flex items-center justify-center">
                  <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                  </svg>
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">No conversation selected</h3>
                <p>Choose a conversation from the sidebar to start messaging</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Messages;
