import React, { useState } from 'react';
import { 
  DollarSign, 
  Shield, 
  AlertCircle, 
  TrendingUp,
  Users,
  Loader2,
  CheckCircle,
  XCircle,
  Wallet
} from 'lucide-react';
import { useOffers, useDomainStatistics } from '../hooks/useDomainData';
import { 
  useDomainTrading,
  useDomaClientStatus,
  useProgressTracker
} from '../hooks/useEnhancedDoma';
import { formatPrice, formatAddress, formatTimeAgo } from '../utils/formatters';

interface OrderbookInterfaceProps {
  domainName: string;
  tokenId?: string;
  contractAddress?: string;
  chainId?: string;
  onOfferCreated?: (orderId: string) => void;
  onListingCreated?: (orderId: string) => void;
}

const EnhancedOrderbookInterface: React.FC<OrderbookInterfaceProps> = ({
  domainName,
  tokenId,
  contractAddress = '0x1234567890123456789012345678901234567890', // Mock address
  chainId = 'eip155:97476', // Doma testnet
  onOfferCreated,
  onListingCreated
}) => {
  // Form state
  const [activeTab, setActiveTab] = useState<'offer' | 'listing' | 'offers'>('offer');
  const [offerAmount, setOfferAmount] = useState('');
  const [offerCurrency, setOfferCurrency] = useState('ETH');
  const [offerExpiry, setOfferExpiry] = useState('168'); // 1 week in hours
  const [listingPrice, setListingPrice] = useState('');
  const [listingCurrency, setListingCurrency] = useState('ETH');
  const [isPrivateOffer, setIsPrivateOffer] = useState(false);

  // Enhanced client integration
  const clientStatus = useDomaClientStatus();
  const trading = useDomainTrading(contractAddress, tokenId || '');
  const { progress, createProgressCallback, reset: resetProgress } = useProgressTracker();

  // Legacy data hooks (for offers and statistics)
  const {
    data: offersData,
    loading: offersLoading,
    refetch: refetchOffers
  } = useOffers(tokenId, {
    take: 10,
    status: 'ACTIVE'
  });

  // Statistics data available but not used in current UI
  // const {
  //   data: statisticsData
  // } = useDomainStatistics(tokenId || '');

  // Mock wallet connection state (for demo)
  const [isWalletConnected, setIsWalletConnected] = useState(false);

  const connectMockWallet = () => {
    setIsWalletConnected(true);
  };

  const handleCreateOffer = async () => {
    if (!offerAmount || !tokenId || !contractAddress) {
      alert('Please fill in all required fields');
      return;
    }

    if (!isWalletConnected && clientStatus.configured) {
      alert('Please connect your wallet to create an offer');
      return;
    }

    try {
      resetProgress();
      const progressCallback = createProgressCallback();

      // Convert offer amount to wei (assuming ETH for demo)
      const priceInWei = (parseFloat(offerAmount) * 1e18).toString();

      // Calculate expiry timestamp
      const expirationTime = Math.floor(Date.now() / 1000) + (parseInt(offerExpiry) * 3600);

      const result = await trading.createOffer.execute({
        tokenId,
        contractAddress,
        price: priceInWei,
        currency: offerCurrency,
        expirationTime,
        isPrivate: isPrivateOffer,
        orderbook: 'DOMA'
      }, progressCallback);

      if (result.success) {
        alert(`Offer created successfully! Order ID: ${result.orderId}`);

        // Reset form
        setOfferAmount('');
        resetProgress();

        // Refresh offers
        refetchOffers();

        if (onOfferCreated) {
          onOfferCreated(result.orderId);
        }
      } else {
        throw new Error(result.error);
      }
    } catch (error) {
      console.error('Failed to create offer:', error);
      alert(`Failed to create offer: ${error instanceof Error ? error.message : 'Unknown error'}`);
      resetProgress();
    }
  };

  const handleCreateListing = async () => {
    if (!listingPrice || !tokenId || !contractAddress) {
      alert('Please fill in all required fields');
      return;
    }

    if (!isWalletConnected && clientStatus.configured) {
      alert('Please connect your wallet to create a listing');
      return;
    }

    try {
      resetProgress();
      const progressCallback = createProgressCallback();

      // Convert listing price to wei (assuming ETH for demo)
      const priceInWei = (parseFloat(listingPrice) * 1e18).toString();

      const result = await trading.createListing.execute({
        tokenId,
        contractAddress,
        price: priceInWei,
        currency: listingCurrency,
        orderbook: 'DOMA'
      }, progressCallback);

      if (result.success) {
        alert(`Listing created successfully! Order ID: ${result.orderId}`);

        // Reset form
        setListingPrice('');
        resetProgress();

        if (onListingCreated) {
          onListingCreated(result.orderId);
        }
      } else {
        throw new Error(result.error);
      }
    } catch (error) {
      console.error('Failed to create listing:', error);
      alert(`Failed to create listing: ${error instanceof Error ? error.message : 'Unknown error'}`);
      resetProgress();
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-lg border border-gray-100 overflow-hidden">
      {/* Header */}
      <div className="border-b border-gray-100 p-6">
        <div className="flex items-center justify-between">
          <h2 className="text-xl font-semibold text-gray-900">Domain Trading</h2>
          <div className="flex items-center gap-2">
            {!clientStatus.configured && (
              <div className="flex items-center text-amber-600 bg-amber-50 px-3 py-1 rounded-full text-sm">
                <AlertCircle className="w-4 h-4 mr-1" />
                Demo Mode
              </div>
            )}
            {clientStatus.configured && (
              <div className="flex items-center text-green-600 bg-green-50 px-3 py-1 rounded-full text-sm">
                <CheckCircle className="w-4 h-4 mr-1" />
                API Ready
              </div>
            )}
          </div>
        </div>

        {/* Progress Indicator */}
        {progress.active && (
          <div className="mt-4 p-3 bg-blue-50 rounded-lg">
            <div className="flex items-center justify-between text-sm text-blue-700 mb-2">
              <span>{progress.step}</span>
              <span>{progress.percentage}%</span>
            </div>
            <div className="w-full bg-blue-200 rounded-full h-2">
              <div 
                className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                style={{ width: `${progress.percentage}%` }}
              />
            </div>
          </div>
        )}
      </div>

      {/* Tab Navigation */}
      <div className="border-b border-gray-100">
        <nav className="flex space-x-8 px-6" aria-label="Tabs">
          {[
            { key: 'offer', label: 'Make Offer', icon: DollarSign },
            { key: 'listing', label: 'Create Listing', icon: TrendingUp },
            { key: 'offers', label: 'View Offers', icon: Users },
          ].map((tab) => (
            <button
              key={tab.key}
              onClick={() => setActiveTab(tab.key as any)}
              className={`${
                activeTab === tab.key
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm flex items-center space-x-2`}
            >
              <tab.icon className="w-4 h-4" />
              <span>{tab.label}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Tab Content */}
      <div className="p-6">
        {/* Status Messages */}
        {!clientStatus.configured && (
          <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
            <div className="flex items-start">
              <Shield className="w-5 h-5 text-blue-600 mt-0.5 mr-3" />
              <div>
                <h4 className="text-sm font-medium text-blue-900">Demo Mode Active</h4>
                <p className="text-sm text-blue-700 mt-1">
                  Trading features are shown for demonstration. To enable real trading, add your Doma API key to the environment configuration.
                </p>
              </div>
            </div>
          </div>
        )}

        {(trading.createOffer.error || trading.createListing.error) && (
          <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
            <div className="flex items-start">
              <XCircle className="w-5 h-5 text-red-600 mt-0.5 mr-3" />
              <div>
                <h4 className="text-sm font-medium text-red-900">Trading Error</h4>
                <p className="text-sm text-red-700 mt-1">
                  {trading.createOffer.error || trading.createListing.error}
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Mock Wallet Connection */}
        {clientStatus.configured && !isWalletConnected && (
          <div className="mb-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
            <div className="flex items-center justify-between">
              <div className="flex items-start">
                <Wallet className="w-5 h-5 text-yellow-600 mt-0.5 mr-3" />
                <div>
                  <h4 className="text-sm font-medium text-yellow-900">Wallet Required</h4>
                  <p className="text-sm text-yellow-700 mt-1">
                    Connect a wallet to enable trading operations.
                  </p>
                </div>
              </div>
              <button
                onClick={connectMockWallet}
                className="ml-4 bg-yellow-600 text-white px-4 py-2 rounded text-sm hover:bg-yellow-700 flex items-center"
              >
                <Wallet className="w-4 h-4 mr-2" />
                Mock Connect
              </button>
            </div>
          </div>
        )}

        {/* Make Offer Tab */}
        {activeTab === 'offer' && (
          <div className="space-y-6">
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Make an Offer for {domainName}</h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Offer Amount
                  </label>
                  <div className="relative">
                    <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                    <input
                      type="number"
                      step="0.001"
                      value={offerAmount}
                      onChange={(e) => setOfferAmount(e.target.value)}
                      className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      placeholder="0.00"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Currency
                  </label>
                  <select
                    value={offerCurrency}
                    onChange={(e) => setOfferCurrency(e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="ETH">ETH</option>
                    <option value="USDC">USDC</option>
                    <option value="WETH">WETH</option>
                  </select>
                </div>
              </div>

              <div className="mt-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Offer Expiry
                </label>
                <select
                  value={offerExpiry}
                  onChange={(e) => setOfferExpiry(e.target.value)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                >
                  <option value="24">24 hours</option>
                  <option value="168">1 week</option>
                  <option value="720">1 month</option>
                  <option value="2160">3 months</option>
                </select>
              </div>

              <div className="mt-6 flex items-center">
                <input
                  type="checkbox"
                  id="private-offer"
                  checked={isPrivateOffer}
                  onChange={(e) => setIsPrivateOffer(e.target.checked)}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                />
                <label htmlFor="private-offer" className="ml-2 block text-sm text-gray-700">
                  Private offer (only visible to domain owner)
                </label>
              </div>

              <div className="mt-8">
                <button
                  onClick={handleCreateOffer}
                  disabled={trading.createOffer.loading || !offerAmount}
                  className="w-full bg-blue-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center justify-center"
                >
                  {trading.createOffer.loading ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Creating Offer...
                    </>
                  ) : (
                    'Submit Offer'
                  )}
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Create Listing Tab */}
        {activeTab === 'listing' && (
          <div className="space-y-6">
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">List {domainName} for Sale</h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Listing Price
                  </label>
                  <div className="relative">
                    <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                    <input
                      type="number"
                      step="0.001"
                      value={listingPrice}
                      onChange={(e) => setListingPrice(e.target.value)}
                      className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
                      placeholder="0.00"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Currency
                  </label>
                  <select
                    value={listingCurrency}
                    onChange={(e) => setListingCurrency(e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent"
                  >
                    <option value="ETH">ETH</option>
                    <option value="USDC">USDC</option>
                    <option value="WETH">WETH</option>
                  </select>
                </div>
              </div>

              <div className="mt-8">
                <button
                  onClick={handleCreateListing}
                  disabled={trading.createListing.loading || !listingPrice}
                  className="w-full bg-green-600 text-white py-3 px-4 rounded-lg font-medium hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center justify-center"
                >
                  {trading.createListing.loading ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Creating Listing...
                    </>
                  ) : (
                    'Create Listing'
                  )}
                </button>
              </div>
            </div>
          </div>
        )}

        {/* View Offers Tab */}
        {activeTab === 'offers' && (
          <div className="space-y-6">
            <h3 className="text-lg font-medium text-gray-900">Current Offers</h3>

            {offersLoading ? (
              <div className="flex justify-center py-8">
                <Loader2 className="w-6 h-6 animate-spin text-blue-600" />
              </div>
            ) : offersData?.items?.length > 0 ? (
              <div className="space-y-4">
                {offersData.items.map((offer: any, index: number) => (
                  <div key={offer.id || index} className="border border-gray-200 rounded-lg p-4">
                    <div className="flex justify-between items-start">
                      <div>
                        <div className="font-medium text-gray-900">
                          {formatPrice(offer.price, offer.currency?.symbol || 'ETH')}
                        </div>
                        <div className="text-sm text-gray-600">
                          From: {formatAddress(offer.offererAddress)}
                        </div>
                        <div className="text-sm text-gray-600">
                          Expires: {formatTimeAgo(offer.expiresAt)}
                        </div>
                      </div>
                      <div className="flex space-x-2">
                        <button className="bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700">
                          Accept
                        </button>
                        <button className="bg-gray-600 text-white px-3 py-1 rounded text-sm hover:bg-gray-700">
                          Counter
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-8 text-gray-500">
                <Users className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                <p>No offers yet for this domain</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default EnhancedOrderbookInterface;
