import { useState, useEffect, useCallback } from 'react';
import { graphqlClient } from '../lib/doma-client';
import {
  GET_DOMAINS,
  GET_DOMAIN,
  GET_NAME_STATISTICS,
  GET_LISTINGS,
  GET_OFFERS,
  GET_TOKEN_ACTIVITIES,
  GET_NAME_ACTIVITIES,
} from '../lib/graphql-queries';

// Types for domain data based on Doma schema
export interface DomainListResponse {
  names: {
    items: DomaName[];
    totalCount: number;
    pageSize: number;
    currentPage: number;
    totalPages: number;
    hasPreviousPage: boolean;
    hasNextPage: boolean;
  };
}

export interface DomaName {
  name: string;
  expiresAt: string;
  tokenizedAt: string;
  eoi: boolean;
  transferLock?: boolean;
  claimedBy?: string;
  registrar: {
    name: string;
    ianaId: string;
  };
  tokens: DomaToken[];
  activities?: DomaNameActivity[];
}

export interface DomaToken {
  tokenId: string;
  networkId: string;
  ownerAddress: string;
  type: string;
  startsAt?: string;
  expiresAt: string;
  createdAt: string;
  explorerUrl: string;
  tokenAddress: string;
  chain: {
    name: string;
    networkId: string;
  };
  listings: DomaListing[];
  activities?: DomaTokenActivity[];
}

export interface DomaListing {
  id: string;
  externalId: string;
  price: string;
  offererAddress: string;
  orderbook: string;
  currency: {
    name: string;
    symbol: string;
    decimals: number;
  };
  expiresAt: string;
  createdAt: string;
}

export interface DomaOffer {
  id: string;
  externalId: string;
  price: string;
  offererAddress: string;
  orderbook: string;
  currency: {
    name: string;
    symbol: string;
    decimals: number;
  };
  expiresAt: string;
  createdAt: string;
  name: string;
  nameExpiresAt: string;
  registrar: {
    name: string;
    ianaId: string;
  };
  tokenId: string;
  tokenAddress: string;
  chain: {
    name: string;
    networkId: string;
  };
}

export interface DomaTokenActivity {
  type: string;
  createdAt: string;
  tokenId?: string;
  txHash?: string;
  networkId?: string;
  finalized?: boolean;
  transferredTo?: string;
  transferredFrom?: string;
  orderId?: string;
  startsAt?: string;
  seller?: string;
  buyer?: string;
  payment?: {
    price: string;
    tokenAddress: string;
    currencySymbol: string;
  };
  orderbook?: string;
  purchasedAt?: string;
}

export interface DomaNameActivity {
  type: string;
  createdAt: string;
  sld?: string;
  tld?: string;
  claimedBy?: string;
  txHash?: string;
  networkId?: string;
}

export interface DomaNameStatistics {
  name: string;
  activeOffers: number;
  offersLast3Days: number;
  highestOffer?: DomaOffer;
}

// Legacy interfaces for backward compatibility
export interface Domain {
  id: string;
  name: string;
  tokenId: string;
  ownerAddress: string;
  createdAt: string;
  expiresAt: string;
  registrationCost: string;
  renewalCost: string;
  isListed: boolean;
  currentPrice?: string;
  listingId?: string;
  offers?: Offer[];
  activities?: Activity[];
}

export interface Offer {
  id: string;
  price: string;
  offerAddress: string;
  expiresAt: string;
  status: string;
  createdAt: string;
}

export interface Activity {
  id: string;
  type: string;
  fromAddress: string;
  toAddress: string;
  price?: string;
  timestamp: string;
  transactionHash: string;
}

export interface DomainStatistics {
  totalDomains: number;
  totalVolume: string;
  totalSales: number;
  averagePrice: string;
  floorPrice: string;
  topSale: string;
}

export interface Listing {
  id: string;
  tokenId: string;
  price: string;
  seller: string;
  buyer?: string;
  status: string;
  createdAt: string;
  expiresAt: string;
  domain: Domain;
}

// Helper function to convert DomaName to legacy Domain format
function convertDomaNameToDomain(domaName: DomaName): Domain {
  const firstToken = domaName.tokens[0];
  const firstListing = firstToken?.listings[0];

  return {
    id: domaName.name,
    name: domaName.name,
    tokenId: firstToken?.tokenId || '',
    ownerAddress: firstToken?.ownerAddress || '',
    createdAt: domaName.tokenizedAt,
    expiresAt: domaName.expiresAt,
    registrationCost: '0', // Not available in schema
    renewalCost: '0', // Not available in schema
    isListed: (firstToken?.listings || []).length > 0,
    currentPrice: firstListing?.price,
    listingId: firstListing?.id,
    offers: [], // Would need separate query
    activities: [], // Would need separate query
  };
}

// Hook to fetch multiple domains with pagination and filtering
export function useDomains(limit: number = 50, skip: number = 0, filters?: any) {
  const [data, setData] = useState<Domain[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchDomains = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      // Use mock data for now until API key is configured
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API delay

      const mockDomains: Domain[] = [
        {
          id: '1',
          name: 'premium.doma',
          tokenId: '12345',
          ownerAddress: '0x1234567890123456789012345678901234567890',
          createdAt: '2024-01-15T10:00:00Z',
          expiresAt: '2025-01-15T10:00:00Z',
          registrationCost: '0.1',
          renewalCost: '0.05',
          isListed: true,
          currentPrice: '2.5',
          listingId: 'listing-123',
          offers: [],
          activities: [],
        },
        {
          id: '2',
          name: 'crypto.doma',
          tokenId: '12346',
          ownerAddress: '0x9876543210987654321098765432109876543210',
          createdAt: '2024-02-01T14:30:00Z',
          expiresAt: '2025-02-01T14:30:00Z',
          registrationCost: '0.08',
          renewalCost: '0.04',
          isListed: true,
          currentPrice: '1.8',
          listingId: 'listing-124',
          offers: [],
          activities: [],
        },
        {
          id: '3',
          name: 'web3.doma',
          tokenId: '12347',
          ownerAddress: '0x5555555555555555555555555555555555555555',
          createdAt: '2024-03-10T09:15:00Z',
          expiresAt: '2025-03-10T09:15:00Z',
          registrationCost: '0.12',
          renewalCost: '0.06',
          isListed: false,
          offers: [],
          activities: [],
        },
        {
          id: '4',
          name: 'nft.doma',
          tokenId: '12348',
          ownerAddress: '0x7777777777777777777777777777777777777777',
          createdAt: '2024-04-05T16:45:00Z',
          expiresAt: '2025-04-05T16:45:00Z',
          registrationCost: '0.15',
          renewalCost: '0.075',
          isListed: true,
          currentPrice: '3.2',
          listingId: 'listing-125',
          offers: [],
          activities: [],
        },
        {
          id: '5',
          name: 'defi.doma',
          tokenId: '12349',
          ownerAddress: '0x8888888888888888888888888888888888888888',
          createdAt: '2024-05-20T11:30:00Z',
          expiresAt: '2025-05-20T11:30:00Z',
          registrationCost: '0.09',
          renewalCost: '0.045',
          isListed: false,
