        },
        {
          id: '6',
          name: 'dao.doma',
          tokenId: '12350',
          ownerAddress: '0x9999999999999999999999999999999999999999',
          createdAt: '2024-06-12T13:20:00Z',
          expiresAt: '2025-06-12T13:20:00Z',
          registrationCost: '0.11',
          renewalCost: '0.055',
          isListed: true,
          currentPrice: '2.1',
